INSERT INTO SYS_ROLES (ID,DESCRIPTION,NAME,ENABLED,CREATED_AT,UPDATED_AT) SELECT SEQ_SYS_ROLES.NEXTVAL,'超级管理员','admin',1,CURRENT_TIMESTAMP(),CURRENT_TIMESTAMP() FROM DUAL WHERE NOT EXISTS(SELECT 1 FROM SYS_ROLES T2 WHERE T2.NAME='admin');
